INSERT INTO `user_profile` (`user_id`, `name`, `proffesional`, `photo`, `photo_background`, `bio`, `social_account`) VALUES
(1,	'Hendri Gunawan',	'Co-Founder & Project Manager',	'hendri-gunawan-4tqv3qd1yfcc3veshuca.jpg',	'hendri-gunawan-aveql9cmubz3grya3dcy.jpg',	'Hello, my name is Hendri Gunawan, I focused on Web design, web development and mobile programming. I like to learn something new about programming.',	'{\"facebook\":\"https:\\/\\/www.facebook.com\\/hendrieg\",\"twitter\":\"\",\"linked_in\":\"https:\\/\\/www.linkedin.com\\/in\\/hendri-gunawan-9a5b1896\",\"dribbble\":\"\",\"email\":\"hendri.gnw@gmail.com\"}'),
(2,	'Sandi Winata',	'Co-Founder & IT Professional',	'',	'',	'My name is Sandi Winata, commonly nicknamed the sandi, I was born in Bogor, June 20, 1997, I was the eldest of two brothers. I just graduated from vocational school in 2015 with the Computer Network program. I am very pleased with the IT world. therefore ',	'{\"facebook\":\"https:\\/\\/www.facebook.com\\/shandhiesansan.difah\",\"twitter\":\"https:\\/\\/twitter.com\\/winatasandi05\",\"linked_in\":\"\",\"dribbble\":\"\",\"email\":\"winatasandi05gmail.com\"}'),
(3,	'Qelopak Teknologi',	'IT Consultant & Training ',	'atc-indonesia-xza70r9xxnshwkjktpua.png',	'atc-indonesia-tx2pmdn5gc0bflkma3b.jpg',	'Qelopak Teknologi Indonesia (http://www.qelopak.com) merupakan salah satu konsultan IT yang terletak di Bogor, Qelopak Indonesia memberikan layanan mengenai jasa pembuatan aplikasi (WEB, Mobile, Dekstop), perancangan dan pemasangan jaringan.',	'{\"facebook\":\"\",\"twitter\":\"\",\"linked_in\":\"\",\"dribbble\":\"\",\"email\":\"\"}');
